(()=>{"use strict";const e=document.getElementById("json-btn"),t=document.getElementById("area-data"),n=document.getElementsByClassName("block")[0];e.addEventListener("click",(()=>{fetch("http://localhost:3000/contacts").then((e=>e.json())).then((e=>{for(let o of e){let e=n.cloneNode(!0);e.id=`block-${o.id}`,e.classList.remove("empty"),e.querySelector("#block-name").innerHTML=o.name,e.querySelector("#block-email").innerHTML=o.email,e.querySelector("#block-phone").innerHTML=o.phone,t.appendChild(e)}}))}))})();